package rbi;
import junit.framework.TestCase;
import java.math.BigInteger;
public class pi extends TestCase {
	//According to http://www.greatplay.net/uselessia/articles/pi2-100000.html
	//The string is too long, you might just see blank, you can check it by copy and paste to a text file.
	public  String PiTo100000 = "3."
			+ ""	
			+ ""
			+ ""
			+ ""
			+ ""
			+ ""
			+ ""
			+ ""
			+ "";
	public String decimalOfPi = "3.";
	public void testPiTo10000() {
		BigInteger TWO = new BigInteger("2");
		BigInteger TEN = new BigInteger("10");
		BigInteger k = new BigInteger("2");
		BigInteger numerator = new BigInteger("4");
		BigInteger den1 = new BigInteger("1");
		BigInteger numofd = new BigInteger("12");
		BigInteger den2 = new BigInteger("4");
		for(int i=-1;i<10000;){
			// Next approximation
			BigInteger p = k.multiply(k);
			BigInteger q = (TWO.multiply(k)).add(BigInteger.ONE);
			k = k.add(BigInteger.ONE);
			BigInteger tempa = numofd;
			BigInteger tempb = den2;
			numofd = (p.multiply(numerator)).add(q.multiply(numofd));
			den2 = (p.multiply(den1)).add(q.multiply(den2));
			numerator = tempa;
			den1 = tempb;
			BigInteger d = numerator.divide(den1);
			BigInteger d1 = numofd.divide(den2);
			while (d.equals(d1)) {
				//100 is the number display in a row, can be change
				if (i%100 == 0){
					System.out.println(); 
				}
				if (i == -1)
					i++;
				else {
					i++;
					System.out.print(decimalOfPi.charAt(i-1));
					decimalOfPi += d.toString();
					assertEquals(decimalOfPi.charAt(i),PiTo100000.charAt(i)); 
				}
				numerator =  TEN.multiply(numerator.mod(den1));
				numofd = TEN.multiply(numofd.mod(den2));
				d = numerator.divide(den1);
				d1 = numofd.divide(den2);
			}
      }
   }
}
