package piTest;
import junit.framework.TestCase;
import java.math.BigInteger;
public class pi extends TestCase {
        // the following lines contain the first 10,000 digits of pi, taken from http://www.greatplay.net/uselessia/articles/pi2-100000.html
        public  String PiTo100000 = "3."
                +""+""+""
                +""+""+""+""
                +""+""+""+""+""
                +""+""+""+""
                +""+""
                +""+""+""+""+""
                +""+""+""+""
                +""+""+""+""
                +""+""+""+""
                +""+"";

        public void testPiTo10000() {
        	int digits = 10000;
        	String myPi = piDigits(digits).toString();
        	myPi = "3."+ myPi.substring(1);//myPi is increased by 10^digits times to become a biginteger, now adding "." to the string
        	for(int i=0;i<digits;i++) {
        		assertEquals(myPi.charAt(i),PiTo100000.charAt(i)); // compares every output character with the hard coded PI string at top of program                   
        		if(i%100==0)
        			System.out.println();
        		System.out.print(myPi.charAt(i));
        	}
        }

        public static BigInteger arcTan( int x, int digits ){
        	int i;
        	int Xsqr = x*x;
        	int D = 0;
        	while( (Math.log(2*D + 3) + (D + 1)*Math.log(Xsqr)) <= digits*Math.log(10) )
        		D++;
        	//  M is the degree of the Taylor polynomial needed to achieve N
        	//  digit accuracy of arctan(1/k).
        	BigInteger tenToN = new BigInteger("1");
        	
        	//  The following loop computes 10^N
        	for( i = 0; i < digits; i++ )
        		tenToN = tenToN.multiply(BigInteger.valueOf(10));
        	int k = 2*D + 1;
        	BigInteger sum = tenToN.divide(BigInteger.valueOf(k)); // sum = (10^N)/c
        	for( i = 0; i < D; i++ ) {
        		k = k - 2;
        		sum = (tenToN.divide(BigInteger.valueOf(k))).
        		subtract(sum.divide(BigInteger.valueOf(Xsqr)));   //  sum = (10^N)/c - s/a
        	} 
        	// return sum/x, which is integer part of 10^N times arctan(1/x)
        	return sum.divide(BigInteger.valueOf(x));
        }

        public static BigInteger piDigits(int digits) {
        	//  To be safe, compute 10 extra digits, to be dropped at end
        	BigInteger pi = new BigInteger("1");  // compiler requires initial
        	pi = (arcTan(5,digits+10).multiply(BigInteger.valueOf(16))).subtract
        	(arcTan(239,digits+10).multiply(BigInteger.valueOf(4)));
        	return pi;  // drop last 8 digits
        } // piDigits
}